mapper.select:
SELECT NVL(COUNT(*), 0) 
FROM LOGINMEMBERS
WHERE ID=#{id}

SELECT *
FROM LOGINMEMBERS
WHERE ID=#{id} AND PWD=#{pwd}

SELECT NVL(COUNT(*), 0)
FROM LOGINMEMBERS
WHERE EMAIL=#{email}

SELECT NVL(COUNT(*), 0)
FROM LOGINMEMBERS
WHERE NICKNAME=#{nickname}

SELECT *
FROM LOGINMEMBERS
WHERE NAME=#{name} and TEL=#{tel}

SELECT *
FROM LOGINMEMBERS
WHERE ID=#{id} and NAME=#{name} and TEL=#{tel}
